mapper.sql:
from
	member a
where 1=1
	AND ifmmDelNy = #{shDelNy}


		AND a.ifmmDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
		AND a.ifmmDateTimeEdit BETWEEN #{shDateStart} AND #{shDateEnd}



		AND ifmmSeq = #{shValue}
		AND ifmmName LIKE CONCAT('%',#{shValue},'%')
		AND ifmmId LIKE CONCAT('%',#{shValue},'%')
		AND ifmmPassword LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
insert into member (
	ifmmName
    ,ifmmNickName
    ,ifmmId
    ,ifmmPassword
    ,ifmmBirth
    ,ifmmGender
    ,ifmmPhone
    ,ifmmEmail
    ,ifmmUseNy
    ,ifmmDelNy
    ,ifmmOrder
    ,ifmmDateTime
    ,ifmmDateTimeEdit
) values (
	#{ifmmName}
	,#{ifmmNickName}
    ,#{ifmmId}
    ,#{ifmmPassword}
    ,#{ifmmBirth}
    ,#{ifmmGender}
    ,#{ifmmPhone}
    ,#{ifmmEmail}
    ,1
    ,0
    ,1
    ,now()
    ,now()
)

		SELECT last_insert_id()

mapper.update:
	update member
  	set
		ifmmName = #{name}
	    ,ifmmNickName = #{nickName}
	    ,ifmmId = #{id}
	    ,ifmmPassword = #{password}
	    ,ifmmEmail = #{email}
	    ,ifmmBirth = #{birthday}
	    ,ifmmGender = #{gender}
	    ,ifmmPhone = #{telephone}
	    ,ifmmDatetimeEdit = now()
  	where 1=1
  		and ifmmSeq = #{ifmmSeq}

mapper.delete:
	delete from member
  	where 1=1
  		and ifmmSeq = #{ifmmSeq}
